import './app.scss';
import React from 'react';
import ReactDOM from 'react-dom';
import Alt from 'alt';
import axios from 'axios';
import { ensureCookiesEnabled } from 'client/utils/cookies';
import {createExampleStore} from 'client/stores/ExampleStore';
import {createExampleActions} from 'client/actions/ExampleActions';
import {createExampleSource} from 'client/sources/ExampleSource';
import HelloWorld from 'client/components/HelloWorld';
import awilix from 'awilix';
const {asValue, asFunction, asClass} = awilix;

const container = awilix.createContainer();

container.register({
  axios: asValue(axios),
  altInstance: asValue(new Alt()),

  exampleStore: asFunction(createExampleStore).singleton(),
  exampleActions: asFunction(createExampleActions).singleton(),
  exampleSource: asFunction(createExampleSource).singleton(),
});

ReactDOM.render(
  ensureCookiesEnabled(
    <HelloWorld actions={container.cradle.exampleActions} store={container.cradle.exampleStore} />
  ),
  document.getElementById('content')
);
