import './app.scss';
import React from 'react';
import ReactDOM from 'react-dom';
import Alt from 'alt';
import axios from 'axios';
import { ensureCookiesEnabled } from 'client/utils/cookies';
import {createExampleStore} from 'client/stores/ExampleStore';
import {createExampleActions} from 'client/actions/ExampleActions';
import {createExampleSource} from 'client/sources/ExampleSource';
import HelloWorld from 'client/components/HelloWorld';

const alt = new Alt();

const exampleActions = createExampleActions(alt);
const exampleSource = createExampleSource(axios, exampleActions);
const exampleStore = createExampleStore(alt, exampleActions, exampleSource);

ReactDOM.render(
  ensureCookiesEnabled(
    <HelloWorld actions={exampleActions} store={exampleStore} />
  ),
  document.getElementById('content')
);
