import assign from 'lodash/assign';
import flatMap from 'lodash/fp/flatMap';
import alt from 'alt-instance';

class GroceryListActions {
  createItem(listId, item) {
    return {listId, item};
  }

  removeItem(listId, itemId) {
    return {listId, itemId};
  }

  toggleItem(listId, itemId) {
    return {listId, itemId};
  }
}

export default assign(
  alt.generateActions('fetchLists', 'loadingLists', 'createList', 'removeList'),
  alt.generateActions(...flatMap(action =>
    [`${action}Success`, `${action}Failure`]
  )(['fetch', 'create', 'remove', 'update'])),
  alt.createActions(GroceryListActions),
);
