import alt from 'client/app/alt';

class MealsActionCreators {
  fetchMeals() {return {};}
  fetchMealsSuccess(mealsList) {return mealsList;}
  fetchMealsFailure(error) {return error;}

  addMeal(date, name, ndbno, quantity) {return {date,name,ndbno,quantity};}
  addMealSuccess(addedMeal) {return addedMeal;}
  addMealFailure(error) {return error;}

  /**
   *
   * @param date - moment.js date object
   */
  changeDate(date) {return date;}

  fetchNutrientsSuccess(ndbno, nutrientsList) {return {ndbno, nutrientsList};}
  fetchAddNutrientsSuccess(ndbno, nutrientsList) {return {ndbno, nutrientsList};}
  fetchNutrientsFailure(error) {return error;}
  fetchAddNutrientsFailure(error) {return error;}
}

export default alt.createActions(MealsActionCreators);
