import alt from 'alt-instance';

class RecipeActions {
  fetchRecipe = (recipeId) => recipeId;
  fetchSuccess = (response) => response;
  fetchFailure = (err) => err;
  loadRecipe = (recipe) => recipe;
  updateRecipe = (key, value) => ({key, value});
  submitSuccess = (response) => response;
  submitFailure = (err) => err;
  updateIngredient = (ingredientId, key, value) => ({ingredientId, key, value});
  removeIngredient = (ingredientId) => ingredientId;
}

export default {
  ...alt.generateActions('clearRecipe', 'submitRecipe', 'addIngredient'),
  ...alt.createActions(RecipeActions)
};
