import React from 'react';
import GroceryListActions from 'actions/GroceryListActions';

import AddInputContainer from 'client/app/components/shared/form/AddInputContainer';
import GroceryListNav from './GroceryListNav';

import './groceries-sidebar.scss';

const GroceryListSidebar = (props) => {
  return (
    <div>
      <AddInputContainer placeholder="Add list..."
                         onAdd={value => GroceryListActions.createList({title: value})} />
      <GroceryListNav onListDelete={listId => GroceryListActions.removeList(listId)}
                      {...props} />
    </div>
  );
};

export default GroceryListSidebar;
