import React from 'react';
import GroceryListActions from 'actions/GroceryListActions';
import GroceryListSchema from 'schemas/GroceryListSchema';

import GroceryList from './GroceryList';

const GroceryListContainer = ({list}) => (
  <GroceryList list={list}
               onItemAdd={value => GroceryListActions.createItem(list._id, {name: value})}
               onItemToggle={itemId => GroceryListActions.toggleItem(list._id, itemId)}
               onItemDelete={itemId => GroceryListActions.removeItem(list._id, itemId)} />
);

GroceryListContainer.propTypes = {
  list: GroceryListSchema.only('_id').isRequired,
};

export default GroceryListContainer;
