import React from 'react';
import {NavItem, Glyphicon} from 'react-bootstrap';

const IconNavItem = (props) => {
  const {glyph, text, className, ...navItemProps} = props;
  const desc = text ? <span className="navbar-icon-desc">{text}</span> : '';
  const style = className ? `${className} navbar-icon` : 'navbar-icon';
  return (
    <NavItem {...navItemProps} className={style}>
      <Glyphicon glyph={glyph} aria-hidden />
      {desc}
    </NavItem>
  );
};

IconNavItem.propTypes = {
  glyph: React.PropTypes.string.isRequired,
  className: React.PropTypes.string,
  text: React.PropTypes.string
};

export default IconNavItem;
