import React from 'react'

import { FormGroup, FormControl, InputGroup, Button, Glyphicon } from 'react-bootstrap'

import './omnibox.scss'

export default class Omnibox extends React.Component {
  constructor(props) {
    super(props);
    this.state = { searchTerm: '' };
    this.onChange = this.onChange.bind(this);
    this.onSearch = this.onSearch.bind(this);
  }

  onChange(e) {
    this.setState({ searchTerm: e.target.value });
  }

  onSearch(e) {
    e.preventDefault();
    this.props.onSearch(this.state.searchTerm);
  }

  render() {
    return (
        <form>
          <FormGroup className="navbar-omnibox">
            <InputGroup>
              <FormControl type="text" placeholder="Search" onChange={this.onChange} />
              <InputGroup.Button>
                <Button type="submit" onClick={this.onSearch}>
                  <Glyphicon glyph="search" aria-label="Search" />
                </Button>
              </InputGroup.Button>
            </InputGroup>
          </FormGroup>
        </form>
    );
  }
}

Omnibox.propTypes = {
  onSearch: React.PropTypes.func.isRequired
};
