import React from 'react';
import {LinkContainer} from 'react-router-bootstrap';
import {NavDropdown, MenuItem, Glyphicon} from 'react-bootstrap';

import {logout} from 'client/sharedjs/auth';

const SettingsDropdown = (props) => {
  return (
    <NavDropdown {...props}
      title={<Glyphicon glyph="cog" />}
      className="navbar-settings-dropdown"
      id="navbar-settings-dropdown"
      aria-hidden>
      <LinkContainer to="/settings">
        <MenuItem>
          <Glyphicon glyph="cog" aria-hidden />
          <span>Settings</span>
        </MenuItem>
      </LinkContainer>
      <LinkContainer to="/profile">
        <MenuItem>
          <Glyphicon glyph="user" aria-hidden />
          <span>Profile</span>
        </MenuItem>
      </LinkContainer>
      <MenuItem divider />
      <LinkContainer to="/notifications">
        <MenuItem>
          <Glyphicon glyph="bell" aria-hidden />
          <span>Notifications</span>
        </MenuItem>
      </LinkContainer>
      <MenuItem divider />
      <MenuItem onClick={logout}>
        <Glyphicon glyph="log-out" aria-hidden />
        <span>Logout</span>
      </MenuItem>
    </NavDropdown>
  );
};

export default SettingsDropdown;
