import React, {PropTypes} from 'react';
import {Grid, Row, Col} from 'react-bootstrap';
import Planner from 'schemas/PlannerSchema';

import './planner.scss';

import PlannerSidebar from './PlannerSidebar';
import PlannerCalendar from './PlannerCalendar';

const PlannerView = ({planner, onRecipeAdd}) => (
  <Grid fluid className="c__planner">
    <Col xs={12} sm={2}>
      <PlannerSidebar onRecipeAdd={onRecipeAdd} />
    </Col>
    <Col xs={12} sm={10}>
      <PlannerCalendar planner={planner} />
    </Col>
  </Grid>
);

PlannerView.propTypes = {
  planner: Planner.isRequired,
  onRecipeAdd: PropTypes.func
};

export default PlannerView;
