import React, {PropTypes} from 'react';
import {FormGroup, InputGroup, FormControl, Button, Glyphicon} from 'react-bootstrap';

import './add-input.scss';

const AddInput = ({value, placeholder, onChange, onSubmit}) => (
  <FormGroup className="s__add-input-group">
    <InputGroup>
      <FormControl type="text"
                   value={value}
                   onChange={event => onChange(event.target.value)}
                   placeholder={placeholder} />
      <InputGroup.Button>
        <Button onClick={onSubmit} aria-label="Add"><Glyphicon glyph="plus" /></Button>
      </InputGroup.Button>
    </InputGroup>
  </FormGroup>
);

AddInput.propTypes = {
  value: PropTypes.string,
  placeholder: PropTypes.string,
  onChange: PropTypes.func,
  onSubmit: PropTypes.func,
};

AddInput.defaultProps = {
  onChange: () => {},
  onSubmit: () => {},
};

export default AddInput;
