import React from 'react';

import AddInput from './AddInput';

const INITIAL_STATE = {value: ''};

export default class AddInputContainer extends React.Component {
  constructor(...args) {
    super(...args);

    this.state = INITIAL_STATE;

    this.valueChanged = this.valueChanged.bind(this);
    this.buttonClicked = this.buttonClicked.bind(this);
  }

  valueChanged(value) {
    this.setState({value});
  }

  buttonClicked() {
    this.props.onAdd(this.state.value);
    this.setState(INITIAL_STATE);
  }

  render() {
    return (
      <AddInput value={this.state.value}
                placeholder={this.props.placeholder}
                onChange={this.valueChanged}
                onSubmit={this.buttonClicked} />
    );
  }
}

AddInputContainer.propTypes = {
  placeholder: React.PropTypes.string,
  onAdd: React.PropTypes.func,
};

AddInputContainer.defaultProps = {
  placeholder: '',
  onAdd: (value) => {}
};
