import { PropTypes } from 'react'
import schema from './Schema'

import ItemSchema from './GroceryListItemSchema'

const { string, number, oneOfType, arrayOf } = PropTypes;

const ListSchema = {
  _id: oneOfType([string, number]).isRequired,
  title: string.isRequired,
  items: arrayOf(ItemSchema).isRequired
};

export default schema(ListSchema);
