import {PropTypes} from 'react';
import schema from './Schema';

const {string, number, oneOfType} = PropTypes;

const Ingredient = {
  _id: oneOfType([string, number]).isRequired,
  name: string.isRequired,
  qty: number,
  unit: string,
  note: string,
};

export const IngredientTypes = schema({
  _id: oneOfType([string, number]),
  name: string,
  qty: number,
  unit: string,
  note: string,
});

export default schema(Ingredient);
