import {PropTypes} from 'react';
import schema from './Schema';

const {string, number, oneOfType, oneOf, shape} = PropTypes;

export const mealTypes = ['breakfast', 'lunch', 'dinner'];

const PlannerEntry = {
  _id: oneOfType([string, number]).isRequired,
  meal: oneOf(mealTypes).isRequired,
  name: string,
  recipe: shape({
    _id: oneOfType([string, number]).isRequired,
    title: string.isRequired,
  })
};

export default schema(PlannerEntry);
