import {PropTypes} from 'react';
import PlannerEntry from './PlannerEntrySchema.js';

const {string, number, arrayOf, oneOfType} = PropTypes;

const Planner = arrayOf(PropTypes.shape({
  _id: oneOfType([string, number]),
  date: string,
  entries: arrayOf(PlannerEntry),
}));

export default Planner;
