import {PropTypes} from 'react';
import schema from './Schema';
import Ingredient, {IngredientTypes} from './IngredientSchema';

const {string, number, arrayOf, oneOfType} = PropTypes;

const Recipe = {
  _id: oneOfType([string, number]),
  title: string.isRequired,
  subtitle: string,
  description: string,
  image: string,
  instructions: string,
  ingredients: arrayOf(Ingredient).isRequired,
};

export const RecipeTypes = schema({
  _id: oneOfType([string, number]),
  title: string,
  subtitle: string,
  description: string,
  image: string,
  instructions: string,
  ingredients: arrayOf(IngredientTypes),
});

export default schema(Recipe);
