import axios from 'axios';

const PLANNER_PATH = '/api/planner';

const PlannerSource = {
  fetch: (date) => axios.get(PLANNER_PATH, {params: {date}}),
  addEntry: (date, entry) => axios.patch(PLANNER_PATH, [{
    op: 'add',
    path: '/entries/-',
    value: entry
  }], {params: {date}})
};

export default PlannerSource;
