import React from 'react';

import FormGroup from 'react-bootstrap/lib/FormGroup';
import FormControl from 'react-bootstrap/lib/FormControl';
import Button from 'react-bootstrap/lib/Button';

class TextInput extends React.Component {
  constructor(props) {
    super();
    this.state = {value: ''};
    this.props = props;
  }

  render() {
    return (<FormControl onChange={e => this.setState({value: e.target.value})} {...this.props} />);
  }
}

export default class NotesView extends React.Component {
  constructor({onLogin}) {
    super();
    this.onLogin = onLogin;
  }

  render() {
    return (
      <FormGroup>
        <TextInput ref="usr" type="text" placeholder="Username" />
        <TextInput ref="passwd" type="password" placeholder="Password" />
        <Button
          type="submit"
          onClick={e => this.onLogin(this.refs.usr.state.value, this.refs.passwd.state.value)
        }>Login</Button>
      </FormGroup>);
  }
}
