
const MEALS_PATH = '/api/example';

export function createExampleSource({axios, exampleActions}) {
  return {
    fetch: () => {
      return axios.get(MEALS_PATH)
        .then(response => {
          const exampleReponse = response.data;
          exampleActions.fetchExampleSuccess(exampleReponse);
        })
        .catch(error => {
          // access response via error.response
          exampleActions.fetchExampleFailure(error);
        });
    }
  };
}
