
import axios from 'axios';
import ExampleActions from 'client/actions/ExampleActions';

const MEALS_PATH = '/api/example';

const ExampleSource = {
  fetch: () => {
    return axios.get(MEALS_PATH)
      .then(response => {
        const exampleReponse = response.data;
        ExampleActions.fetchExampleuccess(exampleReponse);
      })
      .catch(error => {
        // access response via error.response
        ExampleActions.fetchExampleFailure(error);
      });
  }
};

export default ExampleSource;
