
import axios from 'axios';
import MealsActions from 'client/app/actions/MealsActions';

const MEALS_PATH = '/api/example';

const ExamplesSource = {
  fetch: () => {
    return axios.get(MEALS_PATH)
      .then(response => {
        const exampleReponse = response.data;
        ExamplesActions.fetchExampleSuccess(exampleReponse);
      })
      .catch(error => {
        // access response via error.response
        ExamplesActions.fetchExamplesFailure(error);
      });
  }
};

export default MealsSource;
