
export default function build({axios, notesActions, appStore, notesUrl}) {
  return {
    fetch: () => {
      return axios.get(`${notesUrl}/notes`, {headers: {'Token': appStore.token}})
        .then(response => {
          const exampleReponse = response.data;
          notesActions.fetchSuccess(exampleReponse);
        })
        .catch(error => {
          // access response via error.response
          notesActions.fetchFailure(error);
        });
    }
  };
}
