
const MEALS_PATH = '/api/example';

export default function build({axios, notesActions, appStore}) {
  return {
    fetch: () => {
      return axios.get(MEALS_PATH, {headers: {'Token': appStore.token}})
        .then(response => {
          const exampleReponse = response.data;
          notesActions.fetchSuccess(exampleReponse);
        })
        .catch(error => {
          // access response via error.response
          notesActions.fetchFailure(error);
        });
    }
  };
}
