
const LOGIN_URL = '/api/login';

class AppStore {
  constructor(axios, appActions) {
    this.bindListeners({
      onLogin: appActions.LOGIN,
      onLoginSuccess: appActions.LOGIN_SUCCESS,
    });

    this._axios = axios;
    this._actions = appActions;
    this.loggedIn = false;
    this.token = null;
  }

  onLogin({username, password}) {
    this._axios.post(LOGIN_URL, {username, password})
      .then(response => {
        // payload in response.data
        this._actions.loginSuccess(response.data.token);
      })
      .catch(error => {
        // access response via error.response
        this._actions.loginFailure(error.response);
      });
  }

  onLoginSuccess({token}) {
    this.setState({loggedIn: true, token});
  }
}

export default function build({altInstance, appActions, appSource}) {
  return altInstance.createStore(AppStore, 'AppStore', appActions, appSource);
}
