
export function getFrontendConfig() {
  /* inserted by server directly into index.html file */
  return SERVER_CONFIG;
}

export function getNotesUrl() {
  const fc  = getFrontendConfig();
  return `${fc.api_url}/notes?key=${fc.api_key}`;
}

export function getLoginUrl() {
  const fc  = getFrontendConfig();
  return `${fc.api_url}/auth/login?key=${fc.api_key}`;
}
