import jsCookie from 'js-cookie';

/* Maximum cross-browser value for 'expire' parameter */
export const NEVER = new Date('Tue, 19 Jan 2038 03:14:07 GMT');

export function cookiesEnabled() {
  jsCookie.set('testcookie', 'test');
  if(jsCookie.get('testcookie')) {
    jsCookie.remove('testcookie');
    return true;
  } else {
    return false;
  }
}

export function ensureCookiesEnabled(onEnabled) {
  if(cookiesEnabled()) {
    return onEnabled();
  } else {
    return 'You must enable cookies for this application to work';
  }
}
