'use strict';

import mongoose from 'mongoose';

const MealSchema = new mongoose.Schema({
  name: {
    type: String,
    required: true,
  },
  date: {
    type: String,
    required: true,
  },
  ndbno: {
    type: String,
    required: true,
  },
  quantity: {
    type: Number,
    required: true,
  }
});

export default mongoose.model('Meal', MealSchema);
