/**
 * Main application file
 */

'use strict';

import express from 'express';
import config from './config';
import http from 'http';
import morgan from 'morgan';

const app = express();
const server = http.createServer(app);

const env = app.get('env');

/* configure express middlewares */

/* logs requests server-side */
app.use(morgan('dev'));

/* serving client's static files */
app.use('/', express.static(config.clientRoot));


/* configure api routes */
function pageNotFound(req, res) {
  res.status(404);
  res.send("File not found");
}

app.use('/api/example', require('./api/example').default);
app.use('/api/login', require('./api/login').default);
app.route('/api/*').get(pageNotFound);


// Start server
setImmediate(function startServer() {
  app.angularFullstack = server.listen(config.port, config.ip, function() {
    console.log('Express server listening on %d, in %s mode', config.port, app.get('env'));
  });
});

// Expose app
exports = module.exports = app;
