import React from 'react';
import {shallow} from 'enzyme';

import Card from 'components/shared/card/Card';

describe('<Card />', () => {
  const text = 'Text';
  const className = 'custom-class';
  const wrapper = (props) => shallow(<Card className={className} {...props}>{text}</Card>);

  it('renders children', () => {
    const subject = wrapper().text();
    expect(subject).to.equal(text);
  });

  it('renders with s__card class', () => {
    const subject = wrapper().prop('className');
    expect(subject).to.contain('s__card');
  });

  it('renders with custom class', () => {
    const subject = wrapper().prop('className');
    expect(subject).to.contain(className);
  });

  context('with raised prop', () => {
    const raisedWrapper = wrapper({raised: true});
    it('renders with card__raised class', () => {
      const subject = raisedWrapper.prop('className');
      expect(subject).to.contain('card__raised');
    });
  });

  context('without raised prop', () => {
    it('does not render with card__raised class', () => {
      const subject = wrapper().prop('className');
      expect(subject).not.to.contain('card__raised');
    });
  });
});
