import React from 'react';
import {shallow} from 'enzyme';

import CardActions from 'components/shared/card/CardActions';

describe('<CardActions />', () => {
  const className = 'custom-class';
  const Button = () => <button>Action</button>;
  const wrapper = () => shallow(
    <CardActions className={className}>
      <Button />
    </CardActions>
  );

  it('renders with card__actions class', () => {
    const subject = wrapper().prop('className');
    expect(subject).to.contain('card__actions');
  });

  it('renders with custom class', () => {
    const subject = wrapper().prop('className');
    expect(subject).to.contain(className);
  });

  it('renders children', () => {
    const subject = wrapper();
    expect(subject.find(Button)).to.have.lengthOf(1);
  });
});
