import React from 'react';
import {shallow} from 'enzyme';

import Cards from 'components/shared/card/Cards';

describe('<Cards />', () => {
  const Child = () => <p>Child</p>;
  const wrapper = () => shallow(<Cards><Child /></Cards>);

  it('renders with s__cards class', () => {
    const subject = wrapper().prop('className');
    expect(subject).to.contain('s__cards');
  });

  it('renders children', () => {
    const subject = wrapper();
    expect(subject.find(Child)).to.have.lengthOf(1);
  });
});
