
import {ensureCookiesEnabled} from 'client/sharedjs/cookies';

describe('cookies', () => {
  it('should be enabled', () => {
    const cb = sinon.spy();
    ensureCookiesEnabled(cb);
    expect(cb).to.have.been.calledOnce;
  });

  describe('cookies support check', () => {
    it('should not leave cookie after test', () => {
      ensureCookiesEnabled(() => {});
      expect(document.cookie).not.to.contain('testcookie');
    });
  });
});
