import {authenticate} from 'tests/server/shared/setup/authenticate';

require('tests/server/shared/behaviors/api');

describe('GroceryList API:', () => {
  const url = '/api/grocery_lists';
  let token;
  before(async () => {
    token = await authenticate();
  });

  describe('GET /api/grocery_lists', () => {
    itBehavesLike('API GET', {url, getToken: () => token});
  });

  describe('POST /api/grocery_lists', () => {
    itBehavesLike('API POST', {url, factoryName: 'grocery_list', getToken: () => token});
  });

  describe('GET /api/grocery_lists/:id', () => {
    itBehavesLike('API entity GET', {url, factoryName: 'grocery_list', getToken: () => token});
  });

  describe('PUT /api/grocery_lists/:id', () => {
    itBehavesLike('API PUT', {url, factoryName: 'grocery_list', getToken: () => token});
  });

  describe('PATCH /api/grocery_lists/:id', () => {
    itBehavesLike('API PATCH', {
      url,
      factoryName: 'grocery_list',
      property: 'title',
      getToken: () => token
    });
  });

  describe('DELETE /api/grocery_lists/:id', () => {
    itBehavesLike('API DELETE', {url, factoryName: 'grocery_list', getToken: () => token});
  });
});
