'use strict';

var proxyquire = require('proxyquire').noPreserveCache();

var foodGroupCtrlStub = {
  index: 'foodGroupCtrl.index',
};

var routerStub = {
  get: sinon.spy(),
  put: sinon.spy(),
  patch: sinon.spy(),
  post: sinon.spy(),
  delete: sinon.spy(),
};

// require the index with our stubbed out modules
var foodGroupIndex = proxyquire('server/api/food/food_group/index.js', {
  'express': {
    Router() {
      return routerStub;
    },
  },
  './food_group.controller': foodGroupCtrlStub,
}).default;

describe('FoodGroup API Router:', () => {
  it('should return an express router instance', () => {
    foodGroupIndex.should.equal(routerStub);
  });

  describe('GET /api/food_groups', () => {
    it('should route to foodGroup.controller.index', () => {
      routerStub.get
        .withArgs('/', 'foodGroupCtrl.index')
        .should.have.been.calledOnce;
    });
  });
});
